package day_20_forLoops;

public class ForLoop1 {

	public static void main(String[] args) throws InterruptedException {
		// TODO Auto-generated method stub
		//using for loop print "love java" 10 times
		                  //i=i+2         
		for(int i=1; i<=10; i++) {
			System.out.println("Love java!");
		}
		//print number from 5 to 15 using a for loop
		for(int i=5; i<=15; i++) {
			System.out.println(i);
		}
		//for loop- print 20-10 num=num-2 is going to subtract by 2
		for(int num=20; num>=10;num--) {
			System.out.print(num+" ");
			Thread.sleep(456);
		}
		
	}
	
	

}
